import React, { Component } from "react";
import axios from "axios";
import {Link} from 'react-router-dom'

class MyClientsList extends Component {
  state = {
    myClientsList : [],
    isLoading : false
  };

    componentDidMount() {
        this.getMyClientsList();
    }


    getMyClientsList = e => {
        this.setState({isLoading:true})
        axios
            .get("http://localhost:3002/clients/list")
            .then( response =>  this.setState({myClientsList:response.data, isLoading:false }))


  };

  render() {
    if (!this.state.isLoading)
    return (
      <div>
          <fieldset>
            <legend>Mon entreprise</legend>
            <table>
                <tr>
                    <th>
                        <strong>Adresse</strong>
                    </th>
                    <th>
                        <strong>Adresse</strong>
                    </th>
                </tr>
            {this.state.myClientsList.map((el, index) => 
                <div>

                    <h1>{el.name}</h1>
                    <p><strong>Adresse : </strong>{el.street_number} {el.street_name}</p>
                    <p><strong>Code postal : </strong>{el.postal_code}</p>
                    <p><strong>Localité : </strong>{el.location}</p>
                    <p><strong>Ville : </strong>{el.city}</p>
                    <p><strong>Tél : </strong>{el.phone_number_1}</p>
                    <p><strong>Tél : </strong>{el.phone_number_2}</p>
                    <p><strong>E-mail : </strong>{el.mail}</p>
                    <p><strong>Siret : </strong>{el.siret}</p>




                    <Link to={'/modifymyclientsList/'+el.id}><button>Modifier</button></Link>

                    <Link to ={'/'}><button>Retourner au Menu</button></Link>

                    </div>  


            )
        }
        </table>
        </fieldset>
      </div>
    );
    else return (<div> Loading ....</div>)
  }
}

export default MyClientsList;
