import React, { Component } from "react";
import axios from "axios";
import {Link} from 'react-router-dom'

class MyCompany extends Component {
  state = {
    myCompany : [],
    isLoading : false
  };

    componentDidMount() {
        this.getMyCompanyInfo();
    }


    getMyCompanyInfo = e => {
        this.setState({isLoading:true})
        axios
            .get("http://localhost:3002/company/info")
            .then( response =>  this.setState({myCompany:response.data, isLoading:false }))


  };

  render() {
    if (!this.state.isLoading)
    return (
      <div>
          <fieldset>
            <legend>Mon entreprise</legend>
            {this.state.myCompany.map((el, index) => 
                <div>

                    <h1>{el.name}</h1>
                    <p>{el.street_number}</p>
                    <p>{el.street_name}</p>
                    <p>{el.postal_code}</p>
                    <p>{el.location}</p>
                    <p>{el.city}</p>
                    <p>{el.phone_number_1}</p>
                    <p>{el.phone_number_2}</p>
                    <p>{el.mail}</p>
                    <p>{el.siret}</p>




                    <button>Modifier</button>

                    <button onClick={()=>this.handleChangeDelete(el.id)}>Supprimer</button>

                    </div>  


            )
        }

        </fieldset>
      </div>
    );
    else return (<div> Loading ....</div>)
  }
}

export default MyCompany;
