import React, { Component } from "react";
import axios from "axios";
import {Link} from 'react-router-dom'

class MyClientsList extends Component {
  state = {
    myClientsList : [],
    isLoading : false
  };

    componentDidMount() {
        this.getMyClientsList();
    }


    getMyClientsList = e => {
        this.setState({isLoading:true})
        axios
            .get("http://localhost:3002/clients/list")
            .then( response =>  this.setState({myClientsList:response.data, isLoading:false }))


  };

  render() {
    if (!this.state.isLoading)
    return (
      <div>
          <fieldset>
            <legend>Mes clients</legend>
            <table>
                <tr>
                    <th>
                        Nom
                    </th>
                    <th>
                        Adresse
                    </th>
                    <th>
                        Code postal
                    </th>
                    <th>
                        Localité
                    </th>
                    <th>
                        Ville
                    </th>
                    <th>
                        Télephone
                    </th>
                    <th>
                        E-mail
                    </th>
                    <th>
                        date de creation
                    </th>
                </tr>
            {this.state.myClientsList.map((el, index) => 
                <div key = { index }>
                    <tr>
                    <td>{el.name}</td>
                    <td>{el.street_number} {el.street_name}</td>
                    <td>{el.postal_code}</td>
                    <td>{el.lieu}</td>
                    <td>{el.city}</td>
                    <td>{el.phone_number}</td>
                    <td>{el.mail}</td>
                    <td>{el.date_creation}</td>
                    <td><div></div><Link to={'/modifymyclientsList/'+el.id}>Modifier</Link></td>
                    </tr>
                    </div>  


            )
        }
<Link to ={'/'}><button>Retourner au Menu</button></Link>


        </table>
        </fieldset>
      </div>
    );
    else return (<div> Loading ....</div>)
  }
}

export default MyClientsList;
