import React, { Component } from 'react';
import Saisie from '../Saisie'
import axios from 'axios'
import { Link } from 'react-router-dom'

class CreateBills extends Component {
    state = {
        addInputValue: []
      };

      handleChange = e => {
        const addInputValue = { ...this.state.addInputValue }
        addInputValue[e.target.name] = e.target.value;
        this.setState({ addInputValue });
      };


      submitCreateBills = e => {
        e.preventDefault();

        axios
          .post("http://localhost:3002/bills/create", this.state.addInputValue)
          .then(this.setState({}))
         .then(window.history.back() );
        alert("Entreprise créée !")
      };
    render() {


        return (
            <div>
                <form onSubmit={this.submitCreateBills}>
                    <fieldset>
                        <legend>Créer une facture</legend>

                        <Saisie name="description" label="Description: " value={this.state.addInputValue.descriptioin} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="article_number" label="Nombre d'article: " value={this.state.addInputValue.article_number} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="unit_price" label="Prix unitaire: " value={this.state.addInputValue.unit_price} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="amount" label="Montant: " value={this.state.addInputValue.postal_code} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="deposit" label="Accompte: " value={this.state.addInputValue.deposit} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="tva" label="Ville: " value={this.state.addInputValue.tva} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="phone_number_1" label="Telephone n°1: " value={this.state.addInputValue.phone_number_1} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="phone_number_2" label="Telephone n°2: " value={this.state.addInputValue.phone_number_2} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="mail" label="E-mail: " value={this.state.addInputValue.mail} handleChange={this.handleChange} isRequired={false} />
                        <Saisie name="siret" label="Siret: " value={this.state.addInputValue.siret} handleChange={this.handleChange} isRequired={false} />
                        <div> <Link to = {'/mycompany'}><button type="submit">Créer</button></Link></div>
                        <Link to ={'/'}><button>Retourner au Menu</button></Link>



                    </fieldset>
                </form>
            </div>
        )
    }
}
export default CreateBills
