import React, { Component } from 'react'
import axios from 'axios'

export default class ClientsBills extends Component {

    state = { clientsBills: [], isLoading: false }

    getClientsBills = async () => {

        this.setState({ isLoading: true })
        const id = this.props.match.params.id;

        axios
            .get(`http://localhost:3002/bills/listclient/${id}`)
            .then(response => this.setState({ clientsBills: response.data, isLoading: false }))
            .then(console.log(this.state.clientsBills))
    }

    componentDidMount() { this.getClientsBills() }


    render() {
        if (!this.state.isLoading)

            return (
                <div>
                    <fieldset>
            <legend>Mes facture</legend>
            <table>
              <thead>
                <tr>
                  <th>reference</th>
                  <th>accompte</th>
                  <th>Montant de la facture</th>
                  <th>Payé</th>
                  <th>Date de la dernière facture</th>

                </tr>
              </thead>
              <tbody>
                  {this.state.clientsBills.map((el,idx)=>
                  <tr key ={idx}>
                  <td>{el.id}</td>
                  <td>{el.deposit}</td>



                  </tr>

                  )}

                  </tbody>
                  </table>
                  </fieldset>

                </div>
            )
        else return (<div> Loading ....</div>)
    }
}
