import React, { Component } from 'react'
import axios from 'axios'

export default class ClientsBills extends Component {

    state = { clientList : [] , isLoading : false }

    getClientsBills = async () => {
        this.setState({ isLoading: true })
        axios
          .get("http://localhost:3002/clients/list/")
          .then(response => this.setState({ myClientsList: response.data, isLoading: false }))
    }

  render() {
    return (
      <div>

      </div>
    )
  }
}
